mapper.select:
	SELECT
	  hd.deal_amount, hd.deal_year, hd.deal_month, hd.deal_day,
	  CASE
	      WHEN hd.apt_dong REGEXP '^[0-9]+$' THEN hd.apt_dong
	      ELSE NULL
      END AS apt_dong, hd.floor, hd.exclu_use_ar
	FROM housedeal hd
	JOIN houseinfo hi ON hd.apt_seq = hi.apt_seq

	  AND hi.apt_seq = #{sequence}
	  AND hd.exclu_use_ar = #{area}
	  AND STR_TO_DATE(CONCAT(hd.deal_year, '-', LPAD(hd.deal_month, 2, '0'), '-', LPAD(hd.deal_day, 2, '0')), '%Y-%m-%d') > #{since}
	  AND STR_TO_DATE(CONCAT(hd.deal_year, '-', LPAD(hd.deal_month, 2, '0'), '-', LPAD(hd.deal_day, 2, '0')), '%Y-%m-%d') < #{until}

	ORDER BY hd.deal_year DESC, hd.deal_month DESC, hd.deal_day DESC;

SELECT hd.no AS no, hi.apt_seq, hi.apt_nm, hi.build_year, hi.latitude, hi.longitude, hi.sgg_cd, hi.umd_cd, hd.apt_dong, hd.floor, hd.exclu_use_ar AS excluUseAr, hd.deal_amount AS dealAmount, hd.deal_year AS dealYear, hd.deal_month AS dealMonth, hd.deal_day AS dealDay, count_info.totalElements FROM housedeal hd JOIN houseinfo hi ON hd.apt_seq = hi.apt_seq CROSS JOIN ( SELECT COUNT(*) AS totalElements FROM housedeal hd JOIN houseinfo hi ON hd.apt_seq = hi.apt_seq
) count_info
ORDER BY hd.deal_year DESC, hd.deal_month DESC, hd.deal_day DESC LIMIT #{pageSize} OFFSET #{offset}
